package example;

public class AdvanceLoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int data[] = {1,2,4333,222,333};
		//int d=1; 123 
		
		int s=0;
		for(int d : data) {
			//System.out.println(d);
			//.out.println(d/100);
			s += d;
			
		}
		System.out.println(s);
		//System.out.println(data[0]);
	}

}
